styles:
vg-track-selector {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    display: flex;
    justify-content: center;
    width: 50px;
    height: 50px;
    cursor: pointer;
    color: white;
    line-height: 50px;
}
vg-track-selector .container {
    position: relative;
    display: flex;
    flex-grow: 1;
    align-items: center;

    padding: 0;
    margin: 5px;
}
vg-track-selector select.trackSelector {
    width: 50px;
    padding: 5px 8px;
    border: none;
    background: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    color: transparent;
    font-size: 16px;
}
vg-track-selector select.trackSelector:focus {
    outline: none;
}
vg-track-selector .track-selected {
    position: absolute;
    width: 100%;
    height: 50px;
    top: -6px;
    text-align: center;
    text-transform: uppercase;
    font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
    padding-top: 2px;
    pointer-events: none;
}
vg-track-selector .vg-icon-closed_caption:before {
    width: 100%;
}
